import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import TextField from '@material-ui/core/TextField';
import { req, validator, checkForm} from '../../utils'
import host from '../../config'

class CommentsBox extends React.Component {

  state = {
    isFetching: false,
    form: {
      comment: '',
    },
    formErrors: {
      comment: []
    }
  }

  handleSubmit = (e) => {
    e.preventDefault()
    document.querySelector("#message").value = '';
    if (this.state.isFetching) {
      // Alert that already fetching ?
      return;
    }
    checkForm(this.state.form.comment, this.validate, (errors, nbErrors) => {
      this.setState({...this.state, isFetching: true, formErrors: errors})
      if (!nbErrors) {
        console.log("ok")
      }
      else
        console.log("errors")
    })

  }

  onChange = (e) => {
    let name = e.target.name
    this.setState({
      ...this.state,
      [name]: e.target.value,
    }, () => {
      this.setState({
        ...this.state,
        formErrors: {
          ...this.state.formErrors,
          [name]: this.validate(name)
        }
      })
    })
  }

  validate = (name) => {
    validator.value = this.state.form[name]

    if (name === 'comment')
      return validator.notNull().minLen(1).errors
  }


  render() {
    const { locale } = this.props.locales

    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <TextField id="message"
            name="comment"
            label={locale.movie.comment}
            onChange={this.onChange}
            margin="normal"
            fullWidth
          />
        </form>
      </div>
    )
  }
}

const styles = theme => ({
  img: {
    borderRadius:'5px',
    overflow:'hidden'
  },
  paper: {
    background: theme.palette.secondary.dark,
    height:'100%',
    borderRadius: '5px',
    padding:'15px 20px',
    overflow:'hidden'
  },
  frame: {
    margin: '0 -20px -19px -20px',
    width: 'calc(100% + 40px)',
    height: '20vw',
    minHeight: '250px'
  }
})
const mapStateToProps = state => { return state }
let CommentsBoxExport = CommentsBox
CommentsBoxExport = withStyles(styles)(CommentsBoxExport)
CommentsBoxExport = connect(mapStateToProps)(CommentsBoxExport)
export default CommentsBoxExport
