import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import Loading from '../../utils/jsx/Loading'
import TextField from '@material-ui/core/TextField';
import req from '../../utils/req'
import host from '../../config'

class CommentsBox extends React.Component {

  state = {
    isFetching: false,
    comment: ''
  }

  fetchComments = (e) => {
    e.preventDefault()
    this.setState({...this.state, isFetching: true})
    console.log(this.props.auth.user.id)
    req(host + "/api/messages", {
        useToken: true,
        method: "POST",
        body: {
          message: {
              owner: '/api/users/' + this.props.auth.user.id,
              message: this.state.comment,
              movie: '/api/movies/' + this.props.id,
          }
        }
        }).then(res => {
          // Handle api response
          // faire un setState
          console.log(res)
          this.props.fetchComments()
        }).catch(err => {
          console.error(err)
          // Handle errors
        })
  }

  onChange = (e) => {
    let name = e.target.name
    this.setState({
      ...this.state,
      [name]: e.target.value
    })
  }


  render() {
    const { isFetching, comment } = this.state
    const { locale } = this.props.locales

    return (
      <div>
        <Loading display={isFetching}/>
        <form onSubmit={this.fetchComments}>
          <TextField
              name="comment"
              label={locale.movie.comment}
              onChange={this.onChange}
              margin="normal"
              fullWidth
          />
        </form>
      </div>
    )
  }
}

const styles = theme => ({
  img: {
    borderRadius:'5px',
    overflow:'hidden'
  },
  paper: {
    background: theme.palette.secondary.dark,
    height:'100%',
    borderRadius: '5px',
    padding:'15px 20px',
    overflow:'hidden'
  },
  frame: {
    margin: '0 -20px -19px -20px',
    width: 'calc(100% + 40px)',
    height: '20vw',
    minHeight: '250px'
  }
})
const mapStateToProps = state => { return state }
let CommentsBoxExport = CommentsBox
CommentsBoxExport = withStyles(styles)(CommentsBoxExport)
CommentsBoxExport = connect(mapStateToProps)(CommentsBoxExport)
export default CommentsBoxExport
