import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import Loading from '../../utils/jsx/Loading'
import req from '../../utils/req'
import host from '../../config'

class Comments extends React.Component {

  state = {
    isFetching: false
  }

  fetchComments = (id) => {
    this.setState({...this.state, isFetching: true})
    req(`${host}/api/movie/${id}/messages`)
    req(host + '/api/users/' + id, {useToken: true})
    .then(res => {
        console.log(res);
        this.setState({
            isFetching: false,
        })
    })
  }

  componentWillMount() {
    console.log(this.props.id)
    this.fetchComments(this.props.id)
  }

  render() {
    const { isFetching } = this.state
    // const { classes } = this.props
    // const { locale } = this.props.locales

    return (
      <div>
        <Loading display={isFetching}/>

      </div>
    )
  }
}

const styles = theme => ({
  img: {
    borderRadius:'5px',
    overflow:'hidden'
  },
  paper: {
    background: theme.palette.secondary.dark,
    height:'100%',
    borderRadius: '5px',
    padding:'15px 20px',
    overflow:'hidden'
  },
  frame: {
    margin: '0 -20px -19px -20px',
    width: 'calc(100% + 40px)',
    height: '20vw',
    minHeight: '250px'
  }
})
const mapStateToProps = state => { return state }
let CommentsExport = Comments
CommentsExport = withStyles(styles)(CommentsExport)
CommentsExport = connect(mapStateToProps)(CommentsExport)
export default CommentsExport
