import React from 'react'
import { withStyles } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import Loading from '../../utils/jsx/Loading'
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import req from '../../utils/req'
import host from '../../config'
import CommentsBox from './CommentsBox'

class Comments extends React.Component {

  _isMounted = false

  state = {
    isFetching: false,
    comments: [],
    page: 1
  }

  fetchComments = (id) => {
    this.setState({...this.state, isFetching: true})
    req(host + '/api/movies/' + id + '/messages.json?order[id]=DESC&page=' + this.state.page, {useToken: true})
    .then(res => {
         console.log(res);
        this.setState({
            isFetching: false,
            comments: res
        })
        //dispatch les comments 
    }).catch(err => {
      this.setState({
        isFetching: false,
      })
      console.log(err)
    })
  }

  componentWillMount() {
    this.fetchComments(this.props.id)
  }

  componentDidMount() {
    this._isMounted = true
    window.onscroll = () => {
      if (this._isMounted === true) {
        if (window.innerHeight + document.documentElement.scrollTop > document.documentElement.offsetHeight - 200) {
          req(host + '/api/movies/' + this.props.id + '/messages.json?order[id]=DESC'+ '&page=' + this.state.page).then(res => {
            this.setState({
              comments: [res, ...this.state.comments]
            })
            })
        }
      }
    }
  }

  componentWillUnmount() {
    this._isMounted = false
  }

  render() {
    const { isFetching, comments } = this.state
    // const { classes } = this.props
    // const { locale } = this.props.locales

    return (
      <div>
        <CommentsBox id={this.props.id} fetchComments={this.fetchComments}/>
        <Loading display={isFetching}/>
        <List>
        {comments.map(comment => {
            return (
                <ListItem key={comment.id}>
                    <ListItemAvatar>
                        <Avatar src={comment.owner.avatarUrl} />
                    </ListItemAvatar>
                    <ListItemText
                        primary={comment.owner.username}
                        secondary={comment.message}
                    >
                    </ListItemText>
                </ListItem>
            )
        })}
        </List>
      </div>
    )
  }
}

const styles = theme => ({
  img: {
    borderRadius:'5px',
    overflow:'hidden'
  },
  paper: {
    background: theme.palette.secondary.dark,
    height:'100%',
    borderRadius: '5px',
    padding:'15px 20px',
    overflow:'hidden'
  },
  frame: {
    margin: '0 -20px -19px -20px',
    width: 'calc(100% + 40px)',
    height: '20vw',
    minHeight: '250px'
  }
})
const mapStateToProps = state => { return state }
let CommentsExport = Comments
CommentsExport = withStyles(styles)(CommentsExport)
CommentsExport = connect(mapStateToProps)(CommentsExport)
export default CommentsExport
